$.extend({
  getUrlVars: function(iurl){
    var vars = {}, hash;
   // var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
    var hashes = iurl.substring(iurl.indexOf('?') + 1).split('&');
    for(var i = 0; i < hashes.length; i++)
    {
      hash = hashes[i].split('=');
      vars[hash[0]] = hash[1];
     
    }
    return vars;
  }
  
});

function URLToArray(url) {
	var request = {};
	var pairs = url.substring(url.indexOf('?') + 1).split('&');
	for (var i = 0; i < pairs.length; i++) {
		var pair = pairs[i].split('=');
		request[decodeURIComponent(pair[0])] = decodeURIComponent(pair[1]);
	}
	return request;
}

function doRequest(id_base,id_target,aktion,php,to_add,kindof,gif) {
	
	var add="";
	if (id_base!=""){
		add = "&setvalue=" +document.getElementById(id_base).value;
	}
	var set_url = php + ".php?cmd="+aktion+"&aktion="+aktion+to_add+add;
	var url_data = $.getUrlVars(set_url);

	if (kindof == "innerhtml") {
		if (gif != "") {
			document.getElementById(id_target).innerHTML = "";
			document.getElementById(id_target).innerHTML = '<img src="'+gif+'" border="0">';
		}

		$.ajax({
			mode: "abort",
			type: "POST",
			dataType: "html",
			data: url_data,
			url: php + ".php",
			cache: false,
			async:true,
			success: function(html){
				$("#"+id_target).html(html);
			}
		});
	}


	if (kindof == "tovalue") {
		var set_url = php + ".php?cmd="+aktion+"&aktion="+aktion+to_add+add;
	  var url_data = $.getUrlVars(set_url);
		$.ajax({
			mode: "abort",
			type: "POST",
			dataType: "html",
			url: php + ".php",
			data: url_data,
			cache: false,
			async:true,
	 		error:function (xhr, ajaxOptions, thrownError){
                    alert(xhr.status);
                    alert(thrownError);
                }  ,
			success: function(html){
				$("#"+id_target).val(html);
						
			}
		});
		setfocus(id_target);
	}
}

