INSERT INTO `!!TBL_CMS_PREFIX!!temp_content` SET `id`='513', `tid`='30', `lang_id`='2', `content`='<% if ($cmd==\'st\') %>\r\n<div id=\"forum-threads\">\r\n<h1><%$forumtheme.t_name%></h1>\r\n\r\n<% if (count($forum_threads)>0) %>\r\n \r\n <% foreach from=$forum_threads item=fthread %>         \r\n   <%*  <div class=\"row\">\r\n          <div class=\"col-md-2\">Author</div>\r\n          <div class=\"col-md-10\">Beitrag</div>\r\n     </div>\r\n     *%>\r\n     <div class=\"row js-thread-row\">\r\n      <div class=\"col-md-2\">\r\n        <a href=\"#thread-<%$fthread.THREADID%>\" class=\"hidden\"><!----></a>\r\n        <label><% $fthread.user.username %></label>\r\n        <img class=\"img-thumbnail\" src=\"<% $fthread.user.img %>\" alt=\"<% $fthread.user.username %>\" >\r\n        <div>\r\n          <span class=\"small\">Mitglied seit <% $fthread.user.datum_ger %></span>\r\n          <br><a class=\"small\" href=\"#topanker\">nach oben</a>\r\n        </div>  \r\n        \r\n</div>             \r\n<div class=\"col-md-10\">\r\n  <div class=\"row\">\r\n    <div class=\"col-md-6\">\r\n      <span class=\"small\">Verfasst am: <% $fthread.thread_datetime %></span>\r\n    </div>\r\n    <div class=\"col-md-6 text-right\">\r\n        <% if ($customer.kid>0 && ($customer.PERMOD.forum.edit==TRUE || $customer.kid==$fthread.f_kid)) %>\r\n        <a title=\"bearbeiten\" href=\"<%$PHPSELF%>?page=<%$page%>&threadid=<%$fthread.THREADID%>&cmd=answer\">\r\n            <img alt=\"bearbeiten\" title=\"bearbeiten\" src=\"<%$PATH_CMS%>images/opt_edit.png\" ></a>\r\n      <%/if%>  \r\n      <% if ($customer.kid>0 && ($customer.PERMOD.forum.del==TRUE || $customer.kid==$fthread.f_kid)) %> \r\n        <a class=\"js-delthread\" data-tid=\"<%$fthread.THREADID%>\" href=\"javascript:void(0)\">\r\n        <img src=\"<% $PATH_CMS %>images/opt_del.png\" ></a>\r\n      <%/if%>  \r\n    </div>\r\n  </div><!--row-->\r\n<div class=\"row\">\r\n  <div class=\"col-md-12\">\r\n    <div class=\"forum-text\">\r\n      <% $fthread.f_text_bbcode %>\r\n    </div>\r\n    <% if (count($fthread.filelist) > 0)%>\r\n    <h5>Anhnge</h5>\r\n    <table class=\"table table-striped table-hover\">\r\n        <% foreach from=$fthread.filelist item=afile %>\r\n            <tr >\r\n                <td><%$afile.uploadtime%></td>\r\n                <td><%$afile.humanfilesize%></td>\r\n                <td>\r\n                <% if ($afile.ispicture==true && $afile.thumbnail!=\"\") %>\r\n                <a title=\"<%$afile.f_file%>\" rel=\"lytebox[<% $fthread.THREADID %>]\" target=\"_afile\" href=\"<%$PHPSELF%>?page=<%$page%>&cmd=send_img_tobrowser&id=<%$afile.id%>\">\r\n                    <img src=\"<%$afile.thumbnail%>\" alt=\"<%$afile.f_file%> <%$afile.resu%>\" title=\"<%$afile.f_file%> <%$afile.resu%>\" >\r\n                 </a>                   \r\n                <%/if%>\r\n                <% if ($afile.ispicture==false && $afile.thumbnail!=\"\") %>                       \r\n                <a title=\"<%$afile.f_file%>\" target=\"_afile\" href=\"<%$PHPSELF%>?page=<%$page%>&cmd=fdownload&id=<%$afile.id%>\">\r\n                    <img src=\"<%$afile.thumbnail%>\" alt=\"<%$afile.f_file%> <%$afile.resu%>\" title=\"<%$afile.f_file%> <%$afile.resu%>\" >\r\n                 </a> \r\n                 <%/if%>                  \r\n               <% if ($afile.ispicture==false && $afile.thumbnail==\"\") %> \r\n                    <%$afile.f_ext%>\r\n                <%/if%> \r\n                </td>                \r\n                <td class=\"text-right\"><a title=\"<%$afile.f_file|sthsc%>\" href=\"<%$PHPSELF%>?page=<%$page%>&cmd=fdownload&id=<%$afile.id%>\"><i class=\"fa fa-download\"><!----></i></a></td>\r\n            </tr>\r\n            <% /foreach %>\r\n        </table>        \r\n     <% /if %>\r\n  </div>   <!--col12-->\r\n</div><!--row-->\r\n<hr>\r\n<div class=\"row\">\r\n  <div class=\"col-md-6\">\r\n     <% if ($customer.kid>0) %> \r\n      <a class=\"js-freport\" href=\"<%$PHPSELF%>?cmd=freport&page=<%$page%>&threadid=<%$fthread.THREADID%>&themeid=<%$fthread.f_tid%>\"><i class=\"fa fa-bomb\"></i>&nbsp;report</a>\r\n    <%/if%>\r\n  </div>\r\n  <div class=\"col-md-6 text-right\">\r\n    <form action=\"<%$PHPSELF%>?page=<%$page%>&cmd=answer&tid=<%$forumtheme.TID%>\" method=\"post\">\r\n      <button class=\"btn btn-default btn-sm\" type\"submit\"><i class=\"fa fa-comment-o\"><!----></i> antworten</button>\r\n    </form>\r\n  </div>\r\n</div>\r\n\r\n  </div><!--col-->\r\n</div><!--row-->\r\n<div class=\"row\">\r\n  <div class=\"col-md-12\"><hr></div>\r\n</div>\r\n<% /foreach %>\r\n\r\n<%else%>\r\n  <div class=\"alert alert-info\">Es liegen noch keine Beitrge vor.</div>\r\n<%/if%>\r\n</div><!--container-->\r\n<%/if%>', `content_plain`='', `linkname`='', `use_all_lang`='0', `meta_desc`='', `meta_keywords`='', `theme_image`='', `meta_title`='', `t_icon`='', `t_lastchange`='2017-03-16 12:00:45', `t_header_html`='', `t_imgthemealt`='', `t_imgthemetitle`='', `t_themedescription`='', `t_htalinklabel`='_213', `t_breadcrumb`='/', `t_breadcrumb_arr`='a:1:{i:0;a:5:{s:5:\"label\";s:0:\"\";s:6:\"parent\";s:1:\"0\";s:4:\"link\";s:13:\"/de/_213.html\";s:2:\"id\";s:2:\"30\";s:8:\"approved\";s:1:\"1\";}}', `t_themegaltpl`='', `t_themegalid`='9620', `t_ticroppos`='', `t_tiwidth`='0', `t_tiheight`='0', `t_alt_title`='', `t_gblvars`='', `t_tpl_name`='forum_threads', `t_precontent`=''
INSERT INTO `!!TBL_CMS_PREFIX!!temp_content` SET `id`='512', `tid`='9', `lang_id`='2', `content`='<% if ($cmd==\'sf\') %>\r\n\r\n<h2><%$forumobj.fn_name%></h2>\r\n<% if ($customer.kid>0) %> \r\n  <a class=\"btn btn-primary\" href=\"<%$PATH_CMS%>index.php?fid=<%$GET.fid%>&page=<%$page%>&cmd=newtheme\"><i class=\"fa fa-plus\"><!----></i> Neues Thema</a>    \r\n<%/if%>\r\n<% if (count($forum_themes)>0) %>\r\n<table class=\"table table-hover table-striped\">\r\n    <thead>\r\n    <tr>\r\n        <th></hd>\r\n        <th>Thema</th>\r\n        <th>Antworten</th>\r\n        <th>Aufrufe</th>\r\n        <th>Letzter Beitrag</th>\r\n        <th></th>\r\n    </tr>\r\n    </thead>\r\n    <tbody>\r\n    <% foreach from=$forum_themes item=ftheme %> \r\n    <tr>\r\n        <td width=\"22\">\r\n       <% if ($ftheme.hastodaythread==true) %>\r\n           <i class=\"fa fa-file text-danger\"><!----></i>\r\n         <%else%>   \r\n            <i class=\"fa fa-file\"><!----></i>\r\n         <%/if%>        \r\n        </td>\r\n        <td><a href=\"<%$ftheme.themelink%>\"><% $ftheme.t_name %></a></td>\r\n        <td><%$ftheme.THCOUNT%></td>\r\n        <td><%$ftheme.t_hits%></td>\r\n        <td><% if ($ftheme.lastthread.id>0) %> <%$ftheme.lastthread.thread_datetime%> <%else%> \r\n        - <%/if%> </td><td>\r\n<% if ($customer.kid>0 && ($customer.PERMOD.forum.del==TRUE)) %> \r\n    <a onClick=\"return confirm(\'Sind Sie sicher?\')\" href=\"<%$PHPSELF%>?fid=<%$GET.fid%>&page=<%$page%>&tid=<%$ftheme.TID%>&aktion=deltheme\">\r\n    <img src=\"<% $PATH_CMS %>images/opt_del.png\" ></a>\r\n<%/if%>         \r\n</td>\r\n    </tr>\r\n    <%/foreach%>\r\n    </tbody>\r\n</table>\r\n<%else%>\r\n  <div class=\"alert alert-info\">Es liegen noch keine Themen vor.</div>\r\n<%/if%>\r\n<%/if%>\r\n\r\n\r\n<% if ($cmd==\'newtheme\') %>\r\n  <h2>Neuer Beitrag</h2>\r\n  <form action=\"<%$PHPSELF%>\" method=\"post\" enctype=\"multipart/form-data\">\r\n  <div class=\"row\">\r\n    <div class=\"col-md-6\">\r\n    <div class=\"form-group\">\r\n            <label>Themen Titel:</label>\r\n            <input type=\"text\" name=\"FORM[t_name]\" value=\"\" placeholder=\"Theme Titel\" class=\"form-control\">\r\n    </div>\r\n\r\n     \r\n            <textarea id=\"tbMsg\" name=\"FORMTHREAD[f_text]\" style=\"width:100%;height:300px\"><%$forum_thread.f_text%></textarea>\r\n\r\n  <button class=\"btn btn-primary\" type=\"submit\">Senden</button>\r\n    </div><!-- col-md-6 -->\r\n  </div><!-- row -->\r\n  \r\n  <input type=\"hidden\" name=\"cmd\" value=\"savetheme\">\r\n  <input type=\"hidden\" name=\"page\" value=\"<%$page%>\">\r\n  <input type=\"hidden\" name=\"fid\" value=\"<%$forumobj.FID%>\">\r\n  <input type=\"hidden\" name=\"FORM[t_fid]\" value=\"<%$forumobj.FID%>\">\r\n  <input type=\"hidden\" name=\"FORMTHREAD[f_fid]\" value=\"<%$forumobj.FID%>\">\r\n  \r\n  </form>\r\n  \r\n     <script>\r\n    $(function() {\r\n    	$(\"#tbMsg\").sceditor({\r\n    		plugins: \"bbcode\",\r\n    		toolbar: \"bold,italic,underline|pastetext,quote,code|maximize,source\",\r\n    		style: \"minified/jquery.sceditor.default.min.css\",\r\n    		emoticonsRoot : \"<%$forum_path%>js/sceditor/\"\r\n    	});\r\n    });\r\n    </script>\r\n\r\n<%/if%>', `content_plain`='', `linkname`='', `use_all_lang`='0', `meta_desc`='', `meta_keywords`='', `theme_image`='', `meta_title`='', `t_icon`='', `t_lastchange`='2017-03-16 12:00:45', `t_header_html`='', `t_imgthemealt`='', `t_imgthemetitle`='', `t_themedescription`='', `t_htalinklabel`='_214', `t_breadcrumb`='/', `t_breadcrumb_arr`='a:1:{i:0;a:5:{s:5:\"label\";s:0:\"\";s:6:\"parent\";s:1:\"0\";s:4:\"link\";s:13:\"/de/_214.html\";s:2:\"id\";s:1:\"9\";s:8:\"approved\";s:1:\"1\";}}', `t_themegaltpl`='', `t_themegalid`='9620', `t_ticroppos`='', `t_tiwidth`='0', `t_tiheight`='0', `t_alt_title`='', `t_gblvars`='', `t_tpl_name`='forum_themes', `t_precontent`=''
INSERT INTO `!!TBL_CMS_PREFIX!!temp_content` SET `id`='441', `tid`='9990', `lang_id`='2', `content`='<% if ($cmd==\'\' || $cmd==\'load_forum_js\') %> \r\n\r\n<div class=\"alert alert-info text-center\">\r\n  <h4>Forum Neu-Erffnung am 15.09.2016</h4>\r\n</div>\r\n\r\n<% include file=\"forum_latest_threads.tpl\" %>\r\n<h3>Support-Foren</h3>\r\n<% if (count($FORUM.fgroups)>0) %>\r\n<table class=\"table table-striped table-hover\">\r\n    <% foreach from=$FORUM.fgroups item=fgroup %>\r\n    <thead>\r\n      <tr>\r\n          <th></th>\r\n          <th><% $fgroup.fg_name %></th>   \r\n          <th>Themen</th>\r\n          <th>Beitrge</th>    \r\n          <th>letzter Beitrag</th> \r\n          <% if ($FORUM.user.kid>0) %> \r\n          <th></th>\r\n          <%/if%>\r\n      </tr>\r\n    </thead>\r\n    <tbody>\r\n      <% foreach from=$fgroup.foren item=forum %> \r\n      <tr>\r\n          <td width=\"26\">\r\n           <% if ($forum.hastodaythread==true) %>\r\n              <i class=\"fa fa-folder-open-o fa-2x text-danger\" aria-hidden=\"true\"><!----></i>\r\n           <%else%>   \r\n              <i class=\"fa fa-folder-open-o fa-2x\" aria-hidden=\"true\"><!----></i>\r\n           <%/if%>\r\n          </td>\r\n          <td>\r\n            <a href=\"<%$forum.forumlink%>\"><% $forum.fn_name %></a><br>\r\n            <span class=\"small\"><%$forum.fn_shortdesc%></span>\r\n          </td>        \r\n          <td><%$forum.THEMECOUNT%></td>        \r\n          <td><%$forum.THREADCOUNT%></td>\r\n          <td>\r\n            <% if ($forum.lastthread.f_time>0) %>\r\n              <%$forum.lastthread.datetime%>\r\n              <br><span class=\"small\">von <b><%$forum.lastthread.user.username%></b></span>\r\n            <%else%>\r\n              -\r\n            <%/if%>\r\n          </td>\r\n            <% if ($FORUM.user.kid>0) %> \r\n            <td>\r\n                <a class=\"btn btn-default btn-sm\" title=\"Neues Thema erffnen\" href=\"<%$PATH_CMS%>index.php?fid=