INSERT INTO `!!TBL_CMS_PREFIX!!template_pre` SET `tc_cid`='172', `tc_pos`='1', `tc_content`='<style type=\"text/css\">@import url(\"<%$PATH_CMS%>includes/modules/tcblog/css/style.css\");</style>\r\n\r\n\r\n\r\n<section>\r\n<div class=\"innerCell\">\r\n<% if (count($themes)>1) %>\r\n<div class=\"shadetabs\">\r\n<ul>\r\n<% foreach from=$themes item=theme name=mt %>\r\n <li <% $theme.class %> ><a href=\"<% $theme.link %>\"><% $theme.theme %></a></li>\r\n<% /foreach %>\r\n</ul>\r\n</div>\r\n<% /if %>\r\n\r\n<% if ($GET.year>0) %><h3><%$BLOG.daterange%></h3><%/if%>\r\n<% if ($gblitem.perm.add==true) %>\r\n<div style=\"margin:10px 0px 10px 0px;text-align:right\">\r\n<a href=\"javascript:void(0)\" class=\"cmsbtnhref\" onclick=\"dc_show(\'tcblog-ins\',900)\">Beitrag hinzufgen</a>\r\n</div>\r\n<% /if %>\r\n\r\n<div class=\"blog_con\">\r\n<% if ($cmd==\'load_blog_item\') %>\r\n 	<% include file=\"pin_detail.tpl\" %>\r\n<%else%>\r\n    <% if (count($BLOG.items)>0) %>\r\n    <article class=\"blog-post clearfix\">\r\n    <% foreach from=$BLOG.items item=pinitem name=mt %>\r\n        <% include file=\"pin_table.tpl\" %>\r\n    <% /foreach %>\r\n    </article> \r\n       \r\n    <%else%>\r\n        Keine Eintrge\r\n    <% /if %>\r\n<%/if%>    \r\n\r\n    \r\n</div>\r\n\r\n<div class=\"blog_nav\">\r\n<h3>Artikel Tags</h3>\r\n<div class=\"blog-tags\"><% foreach from=$BLOG.tags item=tag %><a href=\"<% $SCRIPT_URI %>?cmd=load_by_tag&tag=<% $tag.tag %>\" title=\"Blog Tags <%$tag.tag%>\"><%$tag.tag%> (<%$tag.count%>)</a><%/foreach%></div>\r\n<h3>Letzte Artikel</h3>\r\n<% foreach from=$BLOG.latestitems item=row %>\r\n    <div>\r\n        <a href=\"<% $SCRIPT_URI %>?cmd=load_blog_item&id=<% $row.DID %>\"><%$row.title%></a><br>\r\n        <span class=\"gray\">Beitrag vom <% $row.date %></span>\r\n    </div>\r\n<% /foreach %>\r\n\r\n<h3>Date Archives</h3>\r\n<% foreach from=$BLOG.yearfilter item=row key=year %>\r\n<ul id=\"blog-nav\">\r\n<li class=\"blog-nav-item\">\r\n<span class=\"blog-nav-year\"><%$year%></span>\r\n    <ul class=\"blog-nav-month-list\">\r\n    <% foreach from=$row item=month key=numm %>\r\n    <% if ($month.count>0) %>\r\n        <li class=\"blog-month\"><a href=\"<% $SCRIPT_URI %>?month=<%$numm%>&year=<% $year %>&cmd=load_ym\"><%$month.label%>(<%$month.count%>)</a></li>\r\n    <%/if%>    \r\n    <%/foreach%>\r\n    </ul>\r\n</li>\r\n<%/foreach%>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"clearer\"></div>\r\n\r\n</div>\r\n</section>\r\n\r\n\r\n<div style=\"display:none\" id=\"tcblog-ins\" class=\"divframe\">\r\n    <% include file=\"pin_insertform.tpl\" %>\r\n</div>\r\n\r\n\r\n\r\n\r\n', `tc_interpreter`='a:0:{}', `tc_tid`='680', `tc_globaltpl`='1'
