INSERT INTO `!!TBL_CMS_PREFIX!!flxtpl_tpl` SET `id`='21', `t_name`='Chart HTML Vorlage', `t_tpl`='<section>\n  <div class=\"container\">\n    <h2><%$flxt.var.fv_berschrift_5%></h2>\n    <div class=\"row\">\n      <div class=\"col-md-6\">\n        <div class=\"chart-container\">\n			    <div id=\"chart-placeholder\" class=\"chart-placeholder\"></div>\n		    </div>\n      </div>\n      <div class=\"col-md-6\">\n        <table class=\"table table-striped table-hover\">\n          <thead>\n            <tr>\n              <th>Datum</th>\n              <th>Wert</th>\n            </tr>\n          </thead>\n          <tfoot>\n            <tr>\n              <td colspan=\"2\" class=\"bigger text-center\">...</td>\n            </tr>\n          </tfoot>\n          <tbody>\n         <% foreach from=$flxt.dataset.chartdiagram item=row name=chartloop %>\n          <% if ($smarty.foreach.chartloop.index <6) %>\n          <tr>\n            <td><%$row.datum_edt.value%></td>\n            <td><%$row.eur_edt.value%></td>\n          </tr>\n          <%/if%>\n          <%/foreach%>\n          </tbody>\n        </table>\n      </div>\n    </div>\n    \n    	\n<script>\n  $(document).ready(function() {\n        var oilprices = [ \n          <%foreach from = $flxt.dataset.chartdiagram item = row %> \n          [stringToDate(\'<%$row.datum_edt.value%>\'), parseFloat(\'<%$row.eur_edt.value%>\')], \n          <%/foreach%>  \n		    ];   \n		    var exchangerates = [\n		        <% foreach from=$flxt.dataset.chartdiagram item=row %>\n		          [stringToDate( \'<%$row.datum_edt.value%>\'),parseFloat(\'<%$row.eurkurs_edt.value%>\')],\n		        <%/foreach %> ];\n		        doPlot(\"right\",oilprices,exchangerates);\n});\n</script>\n  </div>\n</section>', `t_ftid`='34'
