INSERT INTO `!!TBL_PREFIX!!templates` SET description='SU Upload Form', admin='0', approval='1', morder='0', php='modules/safeupload/safeupload.inc', intern_aktion='', c_type='T', block_name='', tl='1', parent='0', gbl_template='1', url_redirect_target='_self', url_redirect='', first_page='0', no_fck='1', tpl_name='safeupload_su-upload-form', layout_group='1', has_perm='0', sf_host='', show_rss_link='0', module_id='content', is_startsite='0', modident='safeupload', is_framework='0', use_framework='1', tid_childs='', gui_frame='1', canonical_url='', perma_link_matrix='', xml_sitemap='1', t_class='', t_attributes='', t_java='function reload_customer_files() {\r\n  simple_load(\'js-su-files\', \'<%$eurl%>cmd=reload_customer_files&cont_matrix_id=\' + $(\'#js-customer-dropzone\').data(\'cont_matrix_id\'));\r\n}\r\n\r\n$( document ).ready(function() {\r\n    reload_customer_files();\r\n var product_pic_drop = new Dropzone(\"#js-customer-dropzone\", { \r\n      paramName: \"datei\",\r\n      clickable: true,\r\n      <%*acceptedFiles: \".jpg,.jpeg,.png,.pdf,.xls,.csv\",*%>\r\n      url:\"<%$eurl%>cmd=su_file_upload&cont_matrix_id=\" + $(\'#js-customer-dropzone\').data(\'cont_matrix_id\'),\r\n      maxFilesize: 32 \r\n    });\r\n    product_pic_drop.on(\"success\", function(file,responseText) {\r\n        product_pic_drop.removeFile(file);\r\n        var result = jQuery.parseJSON(responseText);\r\n        if (result.status==\'failed\') {\r\n            $(\'#dropzonefeedback\').append(\'<p class=\"text-danger\"><i class=\"fa fa-times\"></i> \'+result.filename+\'</p>\');\r\n            show_msge(result.filename);            \r\n        } else {\r\n            $(\'#dropzonefeedback\').append(\'<p class=\"text-success\"><i class=\"fa fa-check-circle-o\"></i> \'+result.filename+\'</p>\');\r\n        }\r\n    });  \r\n    product_pic_drop.on(\"drop\", function() {\r\n         $(\'#js-customer-dropzone\').html(\'\');\r\n         $(\'#dropzonefeedback\').html(\'\');\r\n         $(\'#dropzonefeedback\').show();  \r\n    });   \r\n    product_pic_drop.on(\"queuecomplete\", function() {\r\n         $(\'#js-customer-dropzone\').html(\'Drag & Drop Dateien hier\');\r\n         reload_customer_files();        \r\n    });   \r\n    product_pic_drop.on(\"error\", function(file, message) { \r\n        alert(message);\r\n        this.removeFile(file);           \r\n    });      \r\n});'
